$(NewBinaryConfirm).click(()=>{
    const newName = $(CloneBinaryName).val();
    $.post(`/resources/binaries/clone`,{
        "newName":newName,
        "original":workingEnv.binaryName,
        "contentType":workingEnv.updatedBinary.contentType,
        "owner":workingEnv.updatedBinary.owner,
        "role":workingEnv.updatedBinary.role
    },(status)=>{
       if (status.success){
           PageContent.closeModal(self);
           addAutocompleteValue(workingEnv.PackageName,status.package)
           addAutocompleteValue(workingEnv.BinaryName,newName);
           workingEnv.renderBinary();
       } else {
           PageContent.showModal("Cannot create binary"+JSON.stringify(status.error))
       }
    });
});

