$(self.RightPanel.Upload).click(()=>{
    const formData = new FormData();
    formData.append("data",self.RightPanel.UploadFile.files[0]);
    formData.append("name",workingEnv.binaryName);
    $.ajax({
        url: `/resources/binaries/upload`, 
        method: "POST",
        data: formData,
        processData: false,
        contentType: false,
        success: (binary)=>{
            PageContent.showModal("Upload", "New binary uploaded successfully.");
        }
    });
});

workingEnv.currPage = 0;
workingEnv.BinaryBody = self;


workingEnv.renderBinary = ()=> {
    var downloadFromUrl=(url, filename) =>{
        const a = document.createElement('a');
        a.href = url;
        a.download = filename || '';
        a.style.display = 'none';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
    }


    workingEnv.binaryName = $(LeftPanel.BinaryName).val();
    if (workingEnv.binaryName !== ""){
        $.get(`/resources/binaries/${workingEnv.binaryName}`,(binary)=>{
            workingEnv.updatedBinary = binary;
            $(self.RightPanel.DetailsPanel.Owner).val(binary.owner);
            $(self.RightPanel.DetailsPanel.ContentType).val(binary.contentType);
            if (binary.contentType === "image/png" || 
                binary.contentType === "application/png" ||
                binary.contentType === "application/jpeg"|| 
                binary.contentType === "image/jpeg"){
                $(self.RightPanel.ImgContainer).parent().show();
                self.RightPanel.ImgContainer.Img.src = `/resources/binary/${workingEnv.binaryName}`;
            }else{
                $(self.RightPanel.ImgContainer).parent().hide();
            }
            $(self.RightPanel.DetailsPanel.RoleType).val(binary.role).formSelect();
            M.updateTextFields() ;
            $(self.RightPanel.DetailsPanel.Download).click(()=>{
                downloadFromUrl(`/resources/binary/${workingEnv.binaryName}`,workingEnv.binaryName.substring(workingEnv.binaryName.lastIndexOf("/")+1));
            });
        });
    }
}
$(self.RightPanel.ImgContainer).parent().hide();
$(LeftPanel.BinaryName).change(workingEnv.renderBinary);

self.markChanged=()=>{
    self.LeftPanel.MenuPanel.SaveBinary.markChanged();
}


$(self.RightPanel.DetailsPanel.Owner).on('input',()=>{
    workingEnv.updatedBinary.owner = $(self.RightPanel.DetailsPanel.Owner).val(); 
    self.markChanged();
});

$(self.RightPanel.DetailsPanel.ContentType).on('input',()=>{
    workingEnv.updatedBinary.contentType = $(self.RightPanel.DetailsPanel.ContentType).val(); 
    self.markChanged();
});

$(self.RightPanel.DetailsPanel.RoleType).change(()=>{
    workingEnv.updatedBinary.role = $(self.RightPanel.DetailsPanel.RoleType).val(); 
    self.markChanged();
});

$(self.ok).click(()=>{
    $.ajax({
        url: `/resources/binaries/${workingEnv.binaryName}`, // the resource to delete
        type: 'DELETE',
        success: (result)=> {
            if (result.success){
                location.reload();    
            }else {
                PageContent.showModal("Cannot delete binary "+JSON.stringify(result.error));
            }

        }
});
});

