$(self.RightPanel.Upload).click(()=>{
    const formData = new FormData();
    formData.append("data",self.RightPanel.UploadFile.files[0]);
    formData.append("name",workingEnv.jsName);
    $.ajax({
        url: `/resources/binaries/upload`, 
        method: "POST",
        data: formData,
        processData: false,
        contentType: false,
        success: (js)=>{
            PageContent.showModal("Upload", "New js uploaded successfully.");
        }
    });
});

workingEnv.currPage = 0;
workingEnv.JSBody = self;


workingEnv.renderJS = ()=> {
    var downloadFromUrl=(url, filename) =>{
        const a = document.createElement('a');
        a.href = url;
        a.download = filename || '';
        a.style.display = 'none';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
    }


    workingEnv.jsName = $(LeftPanel.jsName).val();
    if (workingEnv.jsName !== ""){
        $.get(`/resources/binaries/${workingEnv.jsName}`,(js)=>{
            workingEnv.updatedJS = js;
            $.get(`/resources/binary/${workingEnv.jsName}`,(result)=>{
                ace.edit(self.RightPanel.FileContent).setValue(result);
            });
            $(self.LeftPanel.Owner).val(js.owner);
            if (js.contentType === "text/css"){
                ace.edit(self.RightPanel.FileContent).session.setMode("ace/mode/css");
            }
            $(self.LeftPanel.ContentType).val(js.contentType).formSelect();
            $(self.LeftPanel.RoleType).val(js.role).formSelect();
            M.updateTextFields() ;
            $(self.RightPanel.DetailsPanel.Download).click(()=>{
                downloadFromUrl(`/resources/binary/${workingEnv.jsName}`,workingEnv.jsName.substring(workingEnv.jsName.lastIndexOf("/")+1));
            });
        });
    }
}
$(LeftPanel.jsName).change(workingEnv.renderJS);

self.markChanged=()=>{
    self.LeftPanel.MenuPanel.SaveJS.markChanged();
}


$(self.RightPanel.DetailsPanel.Owner).on('input',()=>{
    workingEnv.updatedJS.owner = $(self.RightPanel.DetailsPanel.Owner).val(); 
    self.markChanged();
});

$(self.RightPanel.DetailsPanel.ContentType).change(()=>{
    workingEnv.updatedJS.contentType = $(self.RightPanel.DetailsPanel.ContentType).val(); 
    self.markChanged();
});

$(self.RightPanel.DetailsPanel.RoleType).change(()=>{
    workingEnv.updatedJS.role = $(self.RightPanel.DetailsPanel.RoleType).val(); 
    self.markChanged();
});

