$(self.ok).click(()=>{
    $.ajax({
        url: `/resources/js/${workingEnv.jsName}`, // the resource to delete
        type: 'DELETE',
        success: (result)=> {
            if (result.success){
                location.reload();    
            }else {
                PageContent.showModal("Cannot delete js "+JSON.stringify(result.error));
            }

        }
});
});

$(self.ok).click(()=>{
    const values = Object.entries(workingEnv.updatedJS).map(([key, value]) => ({ key, value }));

   $.post(`/resources/binaries`,{
       "resource" : JSON.stringify(workingEnv.updatedJS) 
    },(status)=>{
        if (status.success){
            PageContent.closeModal(self);
            workingEnv.renderJS();
            workingEnv.SaveJS.markSaved();
        }else {
            PageContent.showModal("Cannot save values "+JSON.stringify(status.error));
        }
    }); 
});

workingEnv.DeletePopup = self;

$(self.ok).click(()=>{
    const responseFx = (status)=>{
            if (status.success){
                PageContent.closeModal(workingEnv.DeletePopup);
                workingEnv.renderJS();
            } else {
                PageContent.showModal(`Cannot delete ${self.key}. ${JSON.stringify(status.error)}`)
            }
        };

    $.ajax({
        "type": 'DELETE',
        "url":`/resources/binary/${workingEnv.jsName}/entries/${self.key}`,
        "success":responseFx
    });
});

self.setKey=(key)=>{
    self.key = key;
}

$(NewJSConfirm).click(()=>{
    const newName = $(JSjsName).val();
    $.post(`/resources/binaries/clone`,{
        "newName":newName,
        "original":workingEnv.jsName,
        "contentType":workingEnv.updatedJS.contentType,
        "owner":workingEnv.updatedJS.owner,
        "role":workingEnv.updatedJS.role
    },(status)=>{
       if (status.success){
           PageContent.closeModal(self);
           addAutocompleteValue(workingEnv.PackageName,status.package)
           addAutocompleteValue(workingEnv.jsName,newName);
           workingEnv.renderJS();
       } else {
           PageContent.showModal("Cannot create js"+JSON.stringify(status.error))
       }
    });
});
