self.process=(type)=>{
    let data = workingEnv.page[type][0];
    if (typeof data === "undefined"){
        $(EnvDetailsContainer.EnvKey).val("");
        $(EnvDetailsContainer.EnvValue).val("");
        EnvGeneralContainer.EnvList.process(type,0);
    }else {
        EnvGeneralContainer.EnvList.process(type,0);
        $(EnvDetailsContainer.EnvKey).val(data.env);
        $(EnvDetailsContainer.EnvValue).val(data.value);

    }

}

self.setType = (type,text)=>{
    $(EnvGeneralContainer.EnvList ).parent().next().html(text);
    $(EnvGeneralContainer.EnvList).change(()=>{
        let data = workingEnv.page[type][$(EnvGeneralContainer.EnvList).val()];
        $(EnvDetailsContainer.EnvKey).val(data.env);
        $(EnvDetailsContainer.EnvValue).val(data.value);
    });
    let updateList = (index)=>{
        EnvGeneralContainer.EnvList.process(type,index);
    }
    $(EnvDetailsContainer.EnvKey).on('input',()=>{
        let index = $(EnvGeneralContainer.EnvList).val()
         workingEnv.page[type][index].env=$(EnvDetailsContainer.EnvKey).val();
         updateList(index);
         markChanged();
    });
    $(EnvDetailsContainer.EnvValue).on('input',()=>{
        let index = $(EnvGeneralContainer.EnvList).val()
         workingEnv.page[type][index].value=$(EnvDetailsContainer.EnvValue).val();
         updateList(index);
         markChanged();
    });

    $(EnvGeneralContainer.AddEnv).click(()=>{
        let newEnv= `new${type}`;
        workingEnv.page[type].push({"env":"","value":newEnv});
        let index = workingEnv.page[type].length;
        EnvGeneralContainer.EnvList.process(type,index-1);
        $(EnvDetailsContainer.EnvKey).val("");
        $(EnvDetailsContainer.EnvValue).val(newEnv);
        markChanged();

    });


    $(EnvGeneralContainer.RemoveEnv).click(()=>{
        let index = parseInt($(EnvGeneralContainer.EnvList).val());
        if (index >= 0){
            workingEnv.page[type].splice(index, 1);
            if (workingEnv.page[type].length>0){
                let data = workingEnv.page[type][0];
                EnvGeneralContainer.EnvList.process(type,0);
                $(EnvDetailsContainer.EnvKey).val(data.env);
                $(EnvDetailsContainer.EnvValue).val(data.value);
            } else {
                $(EnvGeneralContainer.EnvList).empty();
                $(EnvDetailsContainer.EnvKey).val("");
                $(EnvDetailsContainer.EnvValue).val("");
                $(EnvGeneralContainer.EnvList).formSelect();
            }
        }

    });
};

self.process=(type, choosenIndex)=>{
    $(self).empty();
    let data = workingEnv.page[type];
    data.forEach((value,index)=>{
        let env = value.env;
        if (env === "") {
            env = "Default";
        }
        let text = env + " -> " +value.value;
        if (text.length > maxLine) {
            text = text.substring(0, maxLine - 3) + "...";
        } 
        $(self).append($('<option>', {
            value: index,
            text: text,
            selected: index == choosenIndex
        }));
    });
    $(self).formSelect();
}
